const celsiusData=document.querySelector(".celsius > input"),fahrenheitData=document.querySelector(".fahrenheit > input");function roundNumber(e){return Math.round(100*e)/100}function celsiusToFahrenheit(){const e=1.8*parseFloat(celsiusData.value)+32;fahrenheitData.value=roundNumber(e)}function fahrenheitToCelsius(){const e=5/9*(parseFloat(fahrenheitData.value)-32);celsiusData.value=roundNumber(e)}function temperature(){celsiusData.addEventListener("input",celsiusToFahrenheit),fahrenheitData.addEventListener("input",fahrenheitToCelsius)}temperature();